    const FILE_PREVIEW_PATH = 'activities/examples/preview/';
    const FILE_PREVIEW_THUMB_PATH = 'activities/examples/preview/thumbs/';

    public function getParentCategory() {
        $category = ActivityExamplesMaterialsCategoriesTable::getInstance()->find($this->getCategoryId());
        if ($category && $category->getParentCategoryId()) {
            return $this->getParentCategoryId($category->getParentCategoryId());
        }

        return $category->getParentCategoryId();
    }

    private function getParentCategoryId($category_id) {
        $cat = ActivityExamplesMaterialsCategoriesTable::getInstance()->find($category_id);
        if ($cat->getParentCategoryId() != 0) {
            return $this->getParentCategoryId($cat->getParentCategoryId());
        }

        return $cat->getId();
    }

    public function getMaterialFileExt() {
        return pathinfo($this->getMaterialFile(), PATHINFO_EXTENSION);
    }
