class Contacts extends BaseContacts
{
    /**
     * Получить имя пользователя с учетом род. падежа
     * @return string
     */
    public function getUserName() {
        $parental_name = $this->getNameParentalCase();
        if (!empty($parental_name)) {
            return $parental_name;
        }

        return $this->getName();
    }

    /**
     * Получить данные о пользователе на которого переводить сообщения
     */
    public function getRedirectUser() {
        if ($this->redirect_to_id == 0) {
            return null;
        }

        $contact = ContactsTable::getInstance()->createQuery()->where('id = ?', $this->redirect_to_id)->fetchOne();
        if (!$contact) {
            return null;
        }

        return $contact->getUser();
    }
}
