<?php

/**
 * DealerServicesDialogsTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class DealerServicesDialogsTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object DealerServicesDialogsTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('DealerServicesDialogs');
    }

    public static function isActiveForUser(User $user)
    {
        return self::getActiveDialogs($user);
    }

    public static function getLastDialog(User $user)
    {
        $today = strtotime(D::toDb(date('Y-m-d')));
        $serviceDialogs = self::getInstance()
            ->createQuery()
            ->where('status = ?', 1)
            ->andWhere('unix_timestamp(start_date) <= ?', $today)
            ->andWhere('unix_timestamp(end_date) >= ?', $today)
            ->orderBy('id ASC')
            //->limit(1)
            ->execute();

      foreach ($serviceDialogs as $serviceDialog) {
          if ($serviceDialog->isActiveForUser($user)) {
              return $serviceDialog;
          }
      }
        /*return self::getInstance()->createQuery()
                    ->where('status = ?', 1)
                    ->orderBy('id DESC')->limit(1)->fetchOne();*/
                    
      return null;
    }

    private static function getActiveDialogs(User $user)
    {
        $dialogs = array();

        $today = strtotime(D::toDb(date('Y-m-d')));
        $serviceDialogs = self::getInstance()
            ->createQuery()
            ->where('status = ?', 1)
            ->andWhere('unix_timestamp(start_date) <= ?', $today)
            ->andWhere('unix_timestamp(end_date) >= ?', $today)
            ->orderBy('id ASC')
            //->limit(1)
            ->execute();

        foreach ($serviceDialogs as $serviceDialog) {
            if ($serviceDialog->isActiveForUser($user)) {
                $dialogs[] = $serviceDialog;
            }
        }

        return $dialogs;
    }
}