class LogLastRead extends BaseLogLastRead
{
  function markAsRead()
  {
    $this->setLastRead(D::toDb(time(), true));
    $this->save();
    
    LogEntryReadTable::getInstance()
      ->createQuery()
      ->delete()
      ->where('user_id=?', $this->getUserId())
      ->execute();
  }
}